mapper.update:
UPDATE funding
SET title=#{title}, start_date=#{startDate}, exp_date=#{expDate}, target_amount=#{targetAmount}, introduction=#{introduction}
WHERE funding_code = #{fundingCode}

UPDATE funding
SET status_code=#{status}
WHERE funding_code=#{code}

UPDATE funding_main_image
SET name=#{image.name}, size=#{image.size}, location=#{image.location}
WHERE funding_code = #{fundingCode}

mapper.select:
SELECT *
FROM funding_info_image
WHERE funding_code=#{code}
ORDER BY code;

SELECT f.funding_code, id, status, title, start_date, exp_date, target_amount, cml_amount, introduction, code, name, size, location
FROM funding f LEFT OUTER JOIN funding_main_image mi ON f.funding_code = mi.funding_code, funding_status s
WHERE f.status_code = s.status_code AND id=#{id}
ORDER BY funding_code DESC
LIMIT #{pager.offset}, #{pager.perPage}

SELECT COUNT(*)
FROM funding
WHERE id=#{id};
